mapper.select:
select * from likelist where mid = #{lk.mid} and
fecode = #{lk.fecode} 
 tdcode = #{lk.tdcode}
 recode = #{lk.recode}

mapper.delete:
delete from likelist where mid = #{lk.mid} and
fecode = #{lk.fecode} 
 tdcode = #{lk.tdcode}
 recode = #{lk.recode}

